        SIDE_VIEW_ANNOTATION: HidableView.SIDE_VIEW_ANNOTATION,
        SIDE_VIEW_REDACTION_APPLY: HidableView.SIDE_VIEW_REDACTION_APPLY,
        CONTEXT_MENU: HidableView.CONTEXT_MENU,
