        CONTEXT_MENU_UNDERLINE: HidableView.CONTEXT_MENU_UNDERLINE,
        CONTEXT_MENU_SEARCH: HidableView.CONTEXT_MENU_SEARCH,
        CONTEXT_MENU_COPY: HidableView.CONTEXT_MENU_COPY,
        INDICATOR: HidableView.INDICATOR,
